var Emitter = require('./emitter.js');
var Modes = require('./modes.js');
var Util = require('./util.js');
var WebVRPageDialogs = require('./webvr-page-dialogs.js');

 /**
  * Everything having to do with the WebVR button.
  * Emits a 'click' event when it's clicked.
  */
 function WebVRPageButtons(params) {

   this.buttonClasses = {
     button: '-button',      //prefix
     panel: '-panel',        //panels with multiple buttons
     back: '-back',          //back button
     fs: '-fullscreen',      //fullscreen mode button
     vr: '-vr',              //vr mode button
     settings: '-settings'   //settings panel
   };

   this.defaultScale = 0.05; //5% of screen width (may vary).

   this.params = params;

   this.loadIcons_();

 };

 WebVRPageButtons.prototype = new Emitter();

 WebVRPageButtons.prototype.loadIcons_ = function() {
   // Preload some hard-coded SVG.
   this.ICONS = {};
   this.ICONS.cardboard = Util.base64('image/svg+xml', '');
   this.ICONS.fullscreen = Util.base64('image/svg+xml', '');
   this.ICONS.exitFullscreen = Util.base64('image/svg+xml', '');
   this.ICONS.back = Util.base64('image/svg+xml', '');
   this.ICONS.settings = Util.base64('image/svg+xml', '');
 };

 module.exports = WebVRPageButtons;
